      out.write("                countdownEl.textContent = String(mins).padStart(2, '0') + ':' + String(secs).padStart(2, '0');\r\n");
      out.write("                totalSeconds--;\r\n");
      out.write("                setTimeout(updateCountdown, 1000);\r\n");
