      out.write("                    <img src=\"https://down-vn.img.susercontent.com/file/d4bbea4570b93bfd5fc652ca82a262a8\" width=\"100\">\r\n");
      out.write("                    <p class=\"mt-2\">© 2026 Shopee. Tất cả các quyền được bảo lưu.</p>\r\n");
      out.write("                </div>\r\n");
      out.write("            </div>\r\n");
      out.write("        </div>\r\n");
      out.write("\r\n");
      out.write("        <!-- QR Code Generator Library -->\r\n");
      out.write("        <script src=\"https://cdn.jsdelivr.net/npm/qrcodejs@1.0.0/qrcode.min.js\"></script>\r\n");
      out.write("\r\n");
      out.write("        <script>\r\n");
      out.write("            // ==========================================\r\n");
      out.write("            // 1. TOGGLE QR / FORM VIEW\r\n");
