      out.write("                let qrView = document.getElementById('qr-view');\r\n");
      out.write("                let tooltip = document.getElementById('qr-tooltip');\r\n");
      out.write("\r\n");
      out.write("                if (isQR) {\r\n");
      out.write("                    formView.style.display = 'none';\r\n");
      out.write("                    qrView.style.display = 'block';\r\n");
      out.write("                    tooltip.innerHTML = 'Đăng nhập với tài khoản';\r\n");
