      out.write("                    qrView.style.display = 'none';\r\n");
      out.write("                    tooltip.innerHTML = 'Đăng nhập với mã QR';\r\n");
      out.write("                    // Dừng polling khi quay lại form\r\n");
      out.write("                    stopPolling();\r\n");
      out.write("                }\r\n");
      out.write("            }\r\n");
      out.write("\r\n");
      out.write("            // ==========================================\r\n");
      out.write("            // 2. GOOGLE: Direct redirect (href=\"google-login\")\r\n");
      out.write("            // 3. FACEBOOK: Direct redirect (href=\"facebook-login\")\r\n");
      out.write("            // ==========================================\r\n");
      out.write("\r\n");
      out.write("            // ==========================================\r\n");
      out.write("            // 3. QR CODE LOGIN\r\n");
