      out.write("                status.innerHTML = '<i class=\"fas fa-spinner fa-spin\"></i> Đang tạo mã QR...';\r\n");
      out.write("                btnConfirm.style.display = 'none';\r\n");
      out.write("                btnRefresh.style.display = 'none';\r\n");
      out.write("\r\n");
      out.write("                // Gọi API tạo token\r\n");
