      out.write("                                height: 170,\r\n");
      out.write("                                colorDark: '#000000',\r\n");
      out.write("                                colorLight: '#ffffff',\r\n");
      out.write("                                correctLevel: QRCode.CorrectLevel.H\r\n");
      out.write("                            });\r\n");
      out.write("\r\n");
      out.write("                            status.className = 'qr-status pending';\r\n");
      out.write("                            status.innerHTML = '<i class=\"fas fa-qrcode\"></i> Quét mã để đăng nhập';\r\n");
      out.write("                            btnConfirm.style.display = 'inline-block';\r\n");
      out.write("                            btnRefresh.style.display = 'none';\r\n");
      out.write("\r\n");
      out.write("                            // Bắt đầu polling\r\n");
      out.write("                            startPolling();\r\n");
      out.write("                        }\r\n");
      out.write("                    })\r\n");
      out.write("                    .catch(err => {\r\n");
      out.write("                        container.innerHTML = '<i class=\"fas fa-exclamation-triangle text-danger\" style=\"font-size:40px\"></i>';\r\n");
