      out.write("                        status.innerHTML = '<i class=\"fas fa-times-circle\"></i> Lỗi tạo mã QR!';\r\n");
      out.write("                        btnRefresh.style.display = 'inline-block';\r\n");
      out.write("                    });\r\n");
      out.write("            }\r\n");
      out.write("\r\n");
      out.write("            function startPolling() {\r\n");
      out.write("                pollingInterval = setInterval(() => {\r\n");
      out.write("                    if (!qrToken) return;\r\n");
      out.write("\r\n");
      out.write("                    fetch('qr-login?action=check&token=' + qrToken)\r\n");
      out.write("                        .then(r => r.json())\r\n");
      out.write("                        .then(data => {\r\n");
      out.write("                            let status = document.getElementById('qrStatus');\r\n");
      out.write("\r\n");
      out.write("                            if (data.status === 'CONFIRMED') {\r\n");
      out.write("                                stopPolling();\r\n");
      out.write("                                status.className = 'qr-status success';\r\n");
      out.write("                                status.innerHTML = '<i class=\"fas fa-check-circle\"></i> Đăng nhập thành công! Đang chuyển trang...';\r\n");
      out.write("                                document.getElementById('btnConfirmQr').style.display = 'none';\r\n");
      out.write("\r\n");
      out.write("                                // Chuyển trang\r\n");
      out.write("                                setTimeout(() => {\r\n");
      out.write("                                    window.location.href = data.redirect || 'home';\r\n");
      out.write("                                }, 1000);\r\n");
      out.write("                            } else if (data.status === 'EXPIRED') {\r\n");
      out.write("                                stopPolling();\r\n");
      out.write("                                status.className = 'qr-status error';\r\n");
      out.write("                                status.innerHTML = '<i class=\"fas fa-clock\"></i> Mã QR đã hết hạn!';\r\n");
      out.write("                                document.getElementById('btnConfirmQr').style.display = 'none';\r\n");
      out.write("                                document.getElementById('btnRefreshQr').style.display = 'inline-block';\r\n");
      out.write("                            }\r\n");
      out.write("                        });\r\n");
      out.write("                }, 2000); // Polling mỗi 2 giây\r\n");
      out.write("            }\r\n");
      out.write("\r\n");
      out.write("            function stopPolling() {\r\n");
      out.write("                if (pollingInterval) {\r\n");
      out.write("                    clearInterval(pollingInterval);\r\n");
      out.write("                    pollingInterval = null;\r\n");
      out.write("                }\r\n");
      out.write("            }\r\n");
      out.write("\r\n");
      out.write("            function confirmQrScan() {\r\n");
      out.write("                if (!qrToken) return;\r\n");
      out.write("\r\n");
      out.write("                let btn = document.getElementById('btnConfirmQr');\r\n");
      out.write("                btn.disabled = true;\r\n");
      out.write("                btn.innerHTML = '<i class=\"fas fa-spinner fa-spin\"></i> Đang xác nhận...';\r\n");
      out.write("\r\n");
      out.write("                // Giả lập quét QR\r\n");
      out.write("                fetch('qr-login?action=confirm&token=' + qrToken, {\r\n");
      out.write("                    method: 'POST',\r\n");
      out.write("                    headers: { 'Content-Type': 'application/x-www-form-urlencoded' },\r\n");
      out.write("                    body: 'token=' + qrToken + '&action=confirm'\r\n");
      out.write("                })\r\n");
      out.write("                .then(r => r.json())\r\n");
      out.write("                .then(data => {\r\n");
      out.write("                    if (data.success) {\r\n");
      out.write("                        let status = document.getElementById('qrStatus');\r\n");
      out.write("                        status.className = 'qr-status success';\r\n");
      out.write("                        status.innerHTML = '<i class=\"fas fa-mobile-alt\"></i> Quét QR thành công! Đang xác nhận...';\r\n");
      out.write("                    } else {\r\n");
      out.write("                        btn.disabled = false;\r\n");
      out.write("                        btn.innerHTML = '<i class=\"fas fa-mobile-alt\"></i> Giả lập quét QR (Demo)';\r\n");
      out.write("                        alert('Lỗi xác nhận: ' + (data.error || 'Unknown'));\r\n");
      out.write("                    }\r\n");
      out.write("                });\r\n");
      out.write("            }\r\n");
      out.write("        </script>\r\n");
      out.write("    </body>\r\n");
      out.write("\r\n");
